    private void initListeners(){

        signIn_button.addMouseListener(new java.awt.event.MouseAdapter() {
            public void mousePressed(java.awt.event.MouseEvent evt) {
                MousePressed(evt);
            }
        });

        forgotPassword_label.addMouseListener(new java.awt.event.MouseAdapter() {
            public void mouseEntered(java.awt.event.MouseEvent evt) {
                MouseEntered(evt);
            }
            public void mouseExited(java.awt.event.MouseEvent evt) {
                MouseExited(evt);
            }
            public void mousePressed(java.awt.event.MouseEvent evt) {
                MousePressed(evt);
            }
        });

        signUp_label.addMouseListener(new java.awt.event.MouseAdapter() {
            public void mouseEntered(java.awt.event.MouseEvent evt) {
               MouseEntered(evt);
            }
            public void mouseExited(java.awt.event.MouseEvent evt) {
               MouseExited(evt);
            }
            public void mousePressed(java.awt.event.MouseEvent evt) {
               MousePressed(evt);
            }
        });

    }

    private void MouseEntered(java.awt.event.MouseEvent evt){
        if(evt.getSource()==forgotPassword_label){
            forgotPassword_label.setText("<html><u>Forgot&nbsp;Password?</html>");
        }

        if(evt.getSource()==signUp_label){
            signUp_label.setText("<html><u>Sign&nbsp;Up</html>");
        }
    }

    private void MouseExited(java.awt.event.MouseEvent evt){
        if(evt.getSource()==forgotPassword_label){
            forgotPassword_label.setText("Forgot Password?");
        }

        if(evt.getSource()==signUp_label){
            signUp_label.setText("Sign Up");
        }
    }

    private void MousePressed(java.awt.event.MouseEvent evt) {
        
        String uname = username_textfield.getText();
        String pass = String.valueOf(password_textfield.getPassword());
        emptyUnameMessage.setText(null);
        emptyPassMessage.setText(null);

        if(evt.getSource()==signIn_button){

            if(uname.isEmpty()){
                emptyUnameMessage.setText("please enter your username");
            }

            if(pass.isEmpty()){
                emptyPassMessage.setText("please enter your password");
                return;
            }

            boolean isAuthenticated = UserData.authenticateUser(uname, pass);
            
            if(isAuthenticated){

                String id="";
                if(uname.equals("admin")) id="Admin";
                if(UserData.isUserIDExisting(id)){
                    JOptionPane.showMessageDialog(rootPane, "Welcome Admin!");
                    return;

                } else{
                    JOptionPane.showMessageDialog(rootPane, "Welcome Student!");
                    return;  
                }

            }else{
                JOptionPane.showMessageDialog(rootPane, "Username/Password not found");
                return;
            }
        }

        if(evt.getSource()==forgotPassword_label){
            this.setVisible(false);
            new com.mycompany.library.ui.mainpage.LibraryForgotPassResetPageUI().setVisible(true);
        }

        if(evt.getSource()==signUp_label){
            this.setVisible(false);
            new com.mycompany.library.ui.mainpage.LibraryRegisterPageUI().setVisible(true);
        }
    }


